import React from "react";
import "../css/InputSection.css";
import axios from "axios";

class InputSection extends React.Component {
  state = {
    isUploading: false,
    text: "",
    progress: 0,
    file: null,
    error: false,
  };

  onProcess = () => {
    if (this.state.text !== null && this.state.file !== null) {
      //throw error
      this.state.error = true;
    } else {
      const text = {
        inputText: this.state.text,
        upload: this.state.file,
        summarizedText: "",
        completed: false,
      };

      axios.post("http://localhost:8000/api/texts/", text);
    }
  };

  onFileChange = (e) => {
    this.setState({ file: e.target.files[0] });
  };

  render() {
    return (
      <div className="ui container">
        <div className="ui one column grid">
          <div className="row">
            <h3>Enter Text Here</h3>
          </div>
          <div className="row">
            <textarea
              onChange={(e) => {
                this.setState({ text: e.target.value });
              }}
              className="textarea"
              value={this.state.text}
            ></textarea>
          </div>

          <div className="row">
            <button className="fluid ui teal button" onClick={this.onProcess}>
              Process
            </button>
          </div>
        </div>
        <div className="row">
          <div className="ui horizontal divider">Or</div>
        </div>
        <div className="row">
          <div className="ui action input">
            <input type="file" onChange={this.onFileChange} />
            <button className="ui button" onClick={this.onProcess}>
              Process
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default InputSection;
