import React from "react";
import "../css/InputSection.css";
import axios from "axios";

class InputSection extends React.Component {
  state = {
    isUploading: false,
    text: "",
    progress: 0,
    file: null,
    error: false,
  };

  onProcess = () => {
    this.setState({ error: false });
    const text = {
      inputText: this.state.text,
      upload: this.state.file,
      summarizedText: "",
      completed: false,
    };

    axios.post("http://localhost:8000/api/texts/", text);
  };

  onFileChange = (e) => {
    this.setState({ file: e.target.files[0] });
    if (this.state.file === null) {
      this.setState({ error: false });
    }
  };

  handleTextChange = (e) => {
    this.setState({ text: e.target.value });
    if (this.state.text === "") {
      this.setState({ error: false });
    }
  };

  render() {
    if (this.state.text !== null && this.state.file !== null) {
      this.setState({ error: true });
    } else {
      this.setState({ error: false });
    }
    return (
      <div className="ui container">
        <div className="ui one column grid">
          <div className="row">
            <h3>Enter Text Here</h3>
          </div>
          <div className="row">
            <textarea
              onChange={this.handleTextChange}
              className="textarea"
              value={this.state.text}
            ></textarea>
          </div>

          <div className="row">
            <button
              className="fluid ui teal button"
              onClick={this.onProcess}
              disabled={this.state.error}
            >
              Process
            </button>
          </div>
        </div>
        <div className="row">
          <div className="ui horizontal divider">Or</div>
        </div>
        <div className="row">
          <div className="ui action input">
            <input type="file" onChange={this.onFileChange} />
            <button
              className="ui button"
              onClick={this.onProcess}
              disabled={this.state.error}
            >
              Process
            </button>
          </div>
        </div>
        {this.state.error && (
          <div className="row">Either add text or file, not both.</div>
        )}
      </div>
    );
  }
}

export default InputSection;
